import Foundation
import Testing
@testable import Swiftlets

@Test func testBase64URLSafeStringAndBack() {
	let randomBytes = [UInt8](0...UInt8.random(in: 0...254))
	let bytes = [UInt8]("Hello, World! Today is a nice day! éö---+++-+///   ésfdsklj**˚".data(using: .utf8)!) + randomBytes
	let encodedString = Data(bytes).base64URLSafeEncodedString()
	if let decodedData = encodedString?.base64URLSafeDecoded {
		#expect([UInt8](decodedData) == bytes)
	}
	else {
		Issue.record("Failure to decode base64 URL safe string")
	}
}

